"use strict";

const
{
  sequenceIsEmpty,
  sequenceFirst,
  sequenceRest
} = require('../common/sequence');

/**
 * Simple problem solving agent.
 * 
 * Defines problem based on state of the world, searches for a sequence of steps to solve it and execute said sequence of steps.
 * 
 * It is a open loop system - ignores precepts during execution phase. I.e, it is only good for static, discrete, observable, deterministic systems. 
 * 
 * @param {object} parameters
 * @param {State} parameters.state initial state
 * @param {function(state:State, percept:Percept):State} [parameters.updateState] function to update parameters
 * @param {function(state:State):Goal} parameters.formulateGoal function to formulate goal from current state
 * @param {function(state:State,goal:Goal):Problem} [parameters.formulateProblem] function to formulate problem object from state and goal. By default it just puts state and goal into an object.
 * @param {function(problem:Problem):Sequence} parameters.search search for solution to problem
 * @return {function(percept:Percept):Action} an agent function
 */
module.exports.simpleProblemSolvingAgent = function (parameters)
{
  parameters.updateState = parameters.updateState || defaultUpdateState;
  parameters.formulateProblem = parameters.formulateProblem || defaultFormulateProblem;
  return async function (percept)
  {
    parameters.state = await parameters.updateState(parameters.state, percept);
    if (sequenceIsEmpty(parameters.sequence))
    {
      const goal = await parameters.formulateGoal(parameters.state);
      const problem = await parameters.formulateProblem(parameters.state, goal);
      parameters.sequence = await parameters.search(problem)
    }
    if (sequenceIsEmpty(parameters.sequence))
    {
      return null;
    }
    else
    {
      const action = sequenceFirst(parameters.sequence);
      parameters.sequence = sequenceRest(parameters.sequence);
      return action;
    }
  };
}

/**
 * @private
 */
function defaultUpdateState(state, percept)
{
  return state;
}

/**
 * @private
 */
function defaultFormulateProblem(state, goal)
{
  return {
    state,
    goal
  };
}

"use strict";

/**
 * @typedef {Array} Queue
 * @desc Queues are Arrays in this codebase.
 * 
 * Three types of queues are available:
 * <li> FIFO queues
 * <li> LIFO queues
 * <li> Priority queues
 */

/**
 * queue empty test
 */
function queueIsEmpty(queue)
{
  return !queue || !queue.length;
}

/**
 * array.pop
 */
function lifoQueuePop(queue)
{
  return queue.pop()
}

/**
 * array.shift
 */
function fifoQueuePop(queue)
{
  return queue.shift()
}

/**
 * array.push
 */
function queueInsert(element, queue)
{
  if (!queue)
  {
    return [element];
  }
  else
  {
    queue.push(element);
    return queue;
  }
}

/**
 * array.push + sift-up
 */
function priorityQueueInsert(isAbove)
{
  return function (element, queue)
  {
    if (!queue)
    {
      return [element];
    }
    else
    {
      queue.push(element);
      heapSiftUp(queue, queue.length - 1, isAbove);
      return queue;
    }
  }
}

/**
 * sift-up implementation
 */
function heapSiftUp(heap, index, isAbove)
{
  if (index !== 0)
  {
    const parentIndex = index >> 1;
    if (isAbove(heap[index], heap[parentIndex]))
    {
      const tmp = heap[parentIndex];
      heap[parentIndex] = heap[index];
      heap[index] = tmp;
      heapSiftUp(heap, parentIndex, isAbove);
    }
  }
}

/**
 * sift-down implementation
 */
function heapSiftDown(heap, index, isAbove)
{
  const leftIndex = index << 1;
  const rightIndex = leftIndex + 1;
  let maxIndex;
  if (rightIndex >= heap.length)
  {
    if (leftIndex >= heap.length)
    {
      return;
    }
    else
    {
      maxIndex = leftIndex;
    }
  }
  else
  {
    if (isAbove(heap[leftIndex], heap[rightIndex]))
    {
      maxIndex = leftIndex;
    }
    else
    {
      maxIndex = rightIndex;
    }
  }
  if (isAbove(heap[maxIndex], heap[index]))
  {
    const temp = heap[index];
    heap[index] = heap[maxIndex];
    heap[maxIndex] = temp;
    heapSiftDown(heap, maxIndex, isAbove)
  }
}

/**
 * array.shift + array.pop + array.unshift + shft-down
 */
function priorityQueuePop(isAbove)
{
  return function (queue)
  {
    let highest = queue[0];
    queue[0] = queue[queue.length - 1];
    queue.pop();
    heapSiftDown(queue, 0, isAbove);
    return highest;
  };
}

/**
 * plain array; uses array.pop for popping
 */
module.exports.lifoQueue = {
  queueIsEmpty: queueIsEmpty,
  queuePop: lifoQueuePop,
  queueInsert: queueInsert,
}

/**
 * plain array; uses array.shift for popping
 */
module.exports.fifoQueue = {
  queueIsEmpty: queueIsEmpty,
  queuePop: fifoQueuePop,
  queueInsert: queueInsert,
}

/**
 * binary max heap array
 * @param {function(element:object, aboveElement:object):boolean} isAbove order function for priority queue
 * @reutn {object} will return an object similar to lifo/fifoQueue
 */
module.exports.priorityQueue = function (isAbove)
{
  return {
    queueIsEmpty: queueIsEmpty,
    queuePop: priorityQueuePop(isAbove),
    queueInsert: priorityQueueInsert(isAbove),
  };
}

"use strict";

/**
 * @typedef {Array} Sequence
 * 
 */

/**
 * Tests if sequence is empty.
 * 
 * @param {Sequence} sequence
 * @return {boolean}
 */
module.exports.sequenceIsEmpty = function (sequence)
{
  return !sequence || !sequence.length;
}

/**
 * Returns first element of non-empty sequence
 * 
 * @param {Sequence} sequence
 * @return {Action}
 */
module.exports.sequenceFirst = function (sequence)
{
  return sequence[0];
}

/**
 * Returns sequence containing non-first element
 * 
 * @param {Sequence} sequence
 * @return {Action}
 */
module.exports.sequenceRest = function (sequence)
{
  if (sequence && sequence.length > 0)
  {
    return sequence.slice(1);
  }
  else
  {
    return null;
  }
}

"use strict";

module.exports.SlidingBlockPuzzle = class
{
  /**
   * @param {number} width
   * @param {number} height
   */
  constructor(width, height)
  {
    this.width = width;
    this.height = height;
  }

  createRandomInitialState()
  {
    let state = this.createGoalState();

    let steps = 50;

    while (steps-- > 0)
    {
      let actions = this.getActions(state);
      let action = actions[Math.floor(actions.length * Math.random())];
      state = this.performAction(state, action);
    }

    return state;
  }

  createGoalState()
  {
    let state = {
      width: this.width,
      height: this.height,
      length: this.width * this.height
    };
    for (let index = 0; index < state.length; index++)
    {
      state[index] = index;
    }
    return state;
  }

  print(state)
  {
    let output = [];
    for (let yIndex = 0; yIndex < state.height; yIndex++)
    {
      let line = [];
      for (let xIndex = 0; xIndex < state.width; xIndex++)
      {
        line.push(state[yIndex * state.width + xIndex]);
      }
      output.push(line.join(' '))
    }
    return output.join('\n')
  }

  isGoalState(state, goal)
  {
    for (let index = 0; index < state.length; index++)
    {
      if (state[index] !== goal[index])
      {
        return false;
      }
    }
    return true;
  }

  getActions(state)
  {
    const actions = [];
    const emptyIndex = Array.prototype.indexOf.call(state, 0)
    const emptyX = emptyIndex % state.width;
    const emptyY = (emptyIndex - emptyX) / state.width;

    if (emptyX > 0)
    {
      actions.push(emptyIndex - 1)
    }

    if (emptyX + 1 < state.width)
    {
      actions.push(emptyIndex + 1)
    }

    if (emptyY > 0)
    {
      actions.push(emptyIndex - state.width)
    }

    if (emptyY + 1 < state.height)
    {
      actions.push(emptyIndex + state.width)
    }

    return actions;
  }

  performAction(state, action)
  {
    state = Object.assign({}, state);

    const emptyIndex = Array.prototype.indexOf.call(state, 0)

    state[emptyIndex] = state[action];
    state[action] = 0;

    return state;
  }

  stateSerialise(state)
  {
    return Array.prototype.join.call(state, ',');
  }

}

"use strict";

/**
 * Basic uninformed graph search.
 * @param {object} paramaters
 */
module.exports.graphSearch = function (paramaters)
{
  paramaters.generateResult = paramaters.generateResult || defaultGenerateResult;
  paramaters.stepCost = paramaters.stepCost || defaultStepCost;
  return async function (problem)
  {
    let frontier = paramaters.queueInsert({
      state: problem.state,
      stateSerialised: paramaters.stateSerialise(problem.state),
      parent: null,
      cost: 0,
    }, null);
    const explored = {};
    const goal = problem.goal || paramaters.goal;
    while (frontier.length > 0)
    {
      const leaf = paramaters.queuePop(frontier);
      if (paramaters.isGoalState(leaf.state, goal))
      {
        return paramaters.generateResult(leaf);
      }
      else
      {
        explored[leaf.stateSerialised] = true;
      }
      for (let action of paramaters.getActions(leaf.state))
      {
        const resultingState = paramaters.performAction(leaf.state, action);
        const stateSerialised = paramaters.stateSerialise(resultingState);
        if (explored[stateSerialised] === undefined)
        {
          frontier = paramaters.queueInsert({
            state: resultingState,
            action: action,
            stateSerialised: stateSerialised,
            parent: leaf,
            cost: leaf.cost + paramaters.stepCost(leaf.state, action)
          }, frontier);
        }
      }
    }
    return null;
  };
};

function defaultGenerateResult(leaf)
{
  let sequence = [];
  while (leaf)
  {
    if (leaf.action !== undefined)
    {
      sequence.unshift(leaf.action)
    }
    leaf = leaf.parent;
  }
  return sequence;
}

function defaultStepCost(state, action)
{
  return 1;
}

